import { Component } from 'react';
export class BrowserOnly extends Component {
    constructor() {
        super(...arguments);
        this.state = {
            isClient: false
        };
    }
    componentDidMount() {
        this.setState({
            isClient: true
        });
    }
    render() {
        return this.state.isClient ? this.props.children : null;
    }
}
