import { createElement as h } from 'react';
import { storiesOf } from '@storybook/react';
import { Vibrate } from '.';
import ShowDocs from '../ShowDocs';
storiesOf('Generators/Vibrate', module)
    .add('Documentation', () => h(ShowDocs, { md: require('../../docs/en/Vibrate.md') }))
    .add('One beep', () => h("div", null,
    h(Vibrate, { ms: 200 }),
    h("pre", { style: { fontFamily: 'monospace' } }, '<Vibrate ms={200} />')))
    .add('Sequence', () => h("div", null,
    h(Vibrate, { ms: [100, 100, 100, 100, 100] }),
    h("pre", { style: { fontFamily: 'monospace' } }, '<Vibrate ms={[100, 100, 100, 100, 100]} />')));
